  respondToEvent = eventResponse;    if (databaseObject)    {                [super saveObject:^(id<BaseObjectProtocol> data, NSError* error) {            [self addObjectToDatabaseObject:_familyName forKey:FAMILYNAME];            [self addObjectToDatabaseObject:_village forKey:VILLAGE];            [self addObjectToDatabaseObject:_firstName forKey:FIRSTNAME];            [self addObjectToDatabaseObject:[NSNumber numberWithInt:_patient_id] forKey:PATIENTID];            [self addObjectToDatabaseObject:[NSNumber numberWithInt:_weight] forKey:WEIGHT];            [self addObjectToDatabaseObject:[NSNumber numberWithInt:_sex] forKey:SEX];            [self addObjectToDatabaseObject:[NSNumber numberWithInt:_height] forKey:HEIGHT];        }];                respondToEvent(self,nil);    }    else    {        respondToEvent(nil, [self createErrorWithDescription:@"No user has been selected" andErrorCodeNumber:10  inDomain:@"Patient Object"]);    }}-(void)CommonExecution{    NSLog(@"Doesn't need to be implemented Client-side");}-(NSString *)description{    NSString* text = [NSString stringWithFormat:@"\nFirst Name: %@ \nLast Name: %@ \nVillage %@ \nHeight %i \nWeight %i \nID %i \nObjectType: %i",_firstName,_familyName,_village,_height,_weight,_patient_id, self.objectType];    return text;}-(void)createNewPatient:(ObjectResponse)onSuccessHandler{    respondToEvent = onSuccessHandler;    NSNotificationCenter* center = [NSNotificationCenter defaultCenter];    [center addObserver:self selector:@selector(ActionSuccessfull) name:GLOBAL_STATUS_LISTENER object:tempObject];        NSMutableDictionary* dataToSend = [NSMutableDictionary dictionaryWithDictionary:[self consolidateForTransmitting]];        [dataToSend setValue:[NSNumber numberWithInt:kCreateNewPatient] forKey:OBJECTCOMMAND];        [self.appDelegate.ServerManager sendData:dataToSend];}-(void)ActionSuccessfull{        if (tempObject.status == kSuccess) {        // Reset this object with the information brought back through the server        [self unpackageFileForUser:tempObject.data];        // Activate the callback so user knows it was successful        respondToEvent(self, nil);    }else{        respondToEvent(nil,[self createErrorWithDescription:tempObject.errorMessage andErrorCodeNumber:10 inDomain:@"PatientObject"] );    }        // Remove event listener    [[NSNotificationCenter defaultCenter]removeObserver:self];        // Save the new user information that has been returned    [self saveObject:nil];}@end