cationCenter* center = [NSNotificationCenter defaultCenter];    id obj = [super self];    [center addObserver:obj selector:@selector(ActionSuccessfull) name:GLOBAL_STATUS_LISTENER object:tempObject];        NSMutableDictionary* dataToSend = [NSMutableDictionary dictionaryWithDictionary:[self consolidateForTransmitting]];        [dataToSend setValue:[NSNumber numberWithInt:kCreateNewPatient] forKey:OBJECTCOMMAND];        [self.appDelegate.ServerManager sendData:dataToSend];}@end